import React, { useState } from 'react'
import ReactDOM from 'react-dom'

const Statistics = ({ good, setGood, neutral, setNeutral, bad, setBad, all }) => {
  
  <Statistics props={({ good, setGood, neutral, setNeutral, bad, setBad, all })} />
  const setToValue = (value, set) => () => (set(value))
  return (
    <>
      <h1>give feedback</h1>
      <button onClick={setToValue(good + 1, setGood)}>good</button>
      <button onClick={setToValue(neutral + 1, setNeutral)}>neutral</button>
      <button onClick={setToValue(bad + 1, setBad)}>bad</button>
      <h1>statistics</h1>
      <div>
        <tr>good <td> {good} </td> </tr>
        <tr>neutral <td>{neutral}</td></tr>
        <tr>bad <td>{bad}</td></tr>
        <tr>all <td>{all}</td></tr>
        <tr>average <td>{0}</td></tr>
        <tr>positive <td>{good / all}%</td></tr>
      </div>
    </>
  )
}
const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  let all = good+bad+neutral
  const setToValue = (value, set) => () => (set(value))
  return (
    <>
    <h1>give feedback</h1>
    <button onClick={setToValue(good + 1, setGood)}>good</button>
    <button onClick={setToValue(neutral + 1, setNeutral)}>neutral</button>
    <button onClick={setToValue(bad + 1, setBad)}>bad</button>
    <h1>statistics</h1>
    <tr>good <td>{good}</td>
    </tr>
    <tr>neutral <td>{neutral}</td></tr>
    <tr>bad <td>{bad}</td></tr>
    <tr>all <td>{all}</td></tr>
    <tr>average <td>{(good - bad) / all}</td></tr>
    <tr>positive <td>{good / all}%</td></tr>
      
    </>
  )
}

ReactDOM.render(<App />,
  document.getElementById('root')
)
