import React, { useState } from 'react'
import ReactDOM from 'react-dom'

const Statistics = (props) => {
  if(props.all===0){
    return (
      <>
      <h1>No feedback given</h1>
      </>
    )
  }
  return (
    <>
      <table>
        <tbody>
          <tr><td>good</td>
            <td>{props.good}</td></tr>
          <tr><td>neutral</td>
            <td>{props.neutral}</td></tr>
          <tr><td>bad</td>
            <td>{props.bad}</td></tr>
          <tr><td>all</td>
            <td>{props.all}</td></tr>
          <tr><td>average</td>
            <td>{(props.good - props.bad) / props.all}</td>
          </tr>
          <tr><td>positive</td>
            <td>{props.good / props.all}%</td>
          </tr>
        </tbody>
      </table>
    </>
  )
}
const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  let all = 0
  const setToValue = (value, set) => () => (set(value))
  return (
    <>
      <h1>give feedback</h1>
      <button onClick={setToValue(good + 1, setGood)}>good</button>
      <button onClick={setToValue(neutral + 1, setNeutral)}>neutral</button>
      <button onClick={setToValue(bad + 1, setBad)}>bad</button>
      <h1>statistics</h1>
      <Statistics good={good} neutral={neutral} bad={bad} all={all} />
    </>
  )
}

ReactDOM.render(<App />,
  document.getElementById('root')
)
