import React from 'react'
import ReactDOM from 'react-dom'


const Header = (props) => {
  return (
    <>
      <h1>{props.course}</h1>
    </>
  )

}
const Content = (props) => (
  <>
    <Part part={props[0].name} exercises={props[0].exercises} />
    <Part part={props[1].name} exercises={props[1].exercises} />
    <Part part={props[2].name} exercises={props[2].exercises} />
  </>
)
const Part = (props) => (
  <>
    <p>
      {props.part} {props.exercises}
    </p>
  </>
)
const Total = (props) => {
  return (
    <>
      <p>
        Number of exercises {props[0].exercises + props[1].exercises + props[2].exercises}
      </p>
    </>
  )
}
const App = () => {
  const course = 'Half Stack application development'
  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]

  return (
    <div>
      <Header cours={course} />
      <Content parts={parts} />
      <Total parts={parts} />
    </div>
  )
}

ReactDOM.render(<App />, document.getElementById('root'))
