import axios from 'axios'
const base_url='http://localhost:3001/persons'
const getAll = () => {
    const request=axios.get(base_url)
    return request.then(response=>response.data)
}
const add = (person) => {
    const request=axios.post(base_url,person)
    return request.then(response=>response.data)
}
const borrar = (person) => {
    const request=axios.delete(`${base_url}/${person.id}`)
    return request.then(response=>response.data)
}
const actualizar = (persons, setPersons,newName,newPhone) => {
    const person = persons.filter(persona => newName.localeCompare(persona.name))
    const request=axios.put(`${base_url}/${person.id}`, {...person, number: newPhone})
}
export default {getAll, add, borrar, actualizar}
