import axios from 'axios'
const base_url='http://localhost:3001/persons'
const getAll = () => {
    const request=axios.get(base_url)
    return request.then(response=>response.data)
}
const add = ({person}) => {
    const request=axios.post(base_url,person)
    return request.then(response=>response.data)
}
